html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field, print_value, fieldmeta, get_width, get_align_class with context -%}

{% if print_settings.repeat_header_footer %}
    <div id="header-html">
        {% if letter_head and not no_letterhead %}
            <div class="letter-head">{{ letter_head }}</div>
        {% else%}
             <div>
               <table>
                <tr></tr>
               </table>
               </div>
        {% endif %}
   </div>
{% endif %}

<h4 style="text-align: center"><b>بطاقة رصيد موظف</b></h4><br>
<div class="arabic">
    <p>اسم الموظف : {{doc.employee_name}}</p>
    <p>المرتبة : {{frappe.db.get_value("Employee",doc.employee_no,"grade") or ""}}</p>
</div>

<table style="line-height: 1.1; font-size: 13px; width: 100%">
    <tr>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">رقم الصادر</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">التاريخ</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">السابقة</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">الحالية</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">المتبقي</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">الجهة</td>
    </tr>
    {% set btr_list = frappe.get_all('Business Trip Request ST', filters={'employee_no':doc.employee_no,'creation':['<=',doc.creation]}, fields=['name','creation_date','trip_remaining_balance','total_no_of_days','country','saudi_city'], order_by='creation_date asc') %}
    {% for btr in btr_list %}
    <tr>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.name}}</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.creation_date}}</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.trip_remaining_balance}}</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.total_no_of_days}}</td>
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.trip_remaining_balance - btr.total_no_of_days}}</td>
       {% if doc.trip_classification=="External" %}
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.country or ""}}</td>
       {% elif doc.trip_classification=="Internal" %}
       <td class="col-xs-2" style="border:1px solid #000;text-align:center">{{btr.saudi_city or ""}}</td>
       {% endif %}
    </tr>
    {% endfor %}
</table>

{% if print_settings.repeat_header_footer %}
<div id="footer-html">
    {% if footer %}
    <div class="letter-head-footer"> {{ footer }} </div>
    {% endif %}
    <p class="text-center small page-number visible-pdf">
        {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }} </p>
</div>
{% endif %}
