css:
.main-section {
    color:black;
    font-size:13px;
    font-family: 'Arial-MT', Arial, sans-serif;

}

.main-heading {
    color:black;
    font-weight:bold;
    text-align:center;
    margin-bottom:15px;
}

.print-format {
    padding-right: 0.4in !important;
    padding-left: 0.6in !important;
}

.table-heading {
    font-weight:bold;
}

.total-row {
    font-weight:bold;
}

.table {
    width:100%;
    color:black;
    font-size:12px;
    border:1px solid #000 !important;
}

.table tr {
    border:1px solid #000 !important;
}

.table td {
    border:1px solid #000 !important;
    text-align:center;
}

.print-format td {
    padding:4px !important;
}
