css:
@media print {
    .letter-head-footer {
      position: fixed ;
      bottom: 0%;
      left:0% ;
      right:0%;
      z-index:-1;
    }
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field, print_value, fieldmeta, get_width, get_align_class with context -%}

{% if print_settings.repeat_header_footer %}
    <div id="header-html">
        {% if letter_head and not no_letterhead %}
            <div class="letter-head">{{ letter_head }}</div>
        {% else%}
             <div>
               <table>
                <tr></tr>
               </table>
               </div>
        {% endif %}
   </div>
{% endif %}

<div style="font-size:13pt; color:black;">

    <table style="font-size:10pt; color:black; width:100%; border:1px solid black; text-align:center; direction:rtl;">
        <thead>
            <tr>
                <td style="border:1px solid black; vertical-align:middle !important; font-weight:bold;">ﺭﻗﻢ ﺍﻟﻤﻌﺎﻣﻠﺔ</td>
                <td style="border:1px solid black; vertical-align:middle !important;">{{ doc.name }}</td>
                <td style="border:1px solid black; vertical-align:middle !important; font-weight:bold;">التاريخ</td>
                <td style="border:1px solid black; vertical-align:middle !important;">{{ doc.resignation_date }}</td>
                <td style="border:1px solid black; vertical-align:middle !important; font-weight:bold;">ﻧﻮﻉ ﺍﻟﻤﻌﺎﻣﻠﺔ</td>
                <td style="border:1px solid black; vertical-align:middle !important;">طلب استقالة</td>
            </tr>

            <tr>
                <td style="border:1px solid black; vertical-align:middle !important;" colspan="1">ﺍﻟﻤﻮﺿﻮﻉ</td>
                <td style="border:1px solid black; vertical-align:middle !important;" colspan="5">طلب استقالة ({{doc.employee_name}}) من الهيئة العامة للإحصاء</td>
            </tr>
        </thead>
    </table>

    {% set employee_department = frappe.db.get_value("Employee", doc.employee_no, "department") %}
    {% set main_department_manager = frappe.db.get_value("Department", employee_department, "custom_main_department_manager") %}
    {% set manager = frappe.db.get_value("Employee", main_department_manager, "employee_name") %}

    {% set workflow_data = frappe.db.get_all("Workflow Action", {"reference_doctype" : "Employee Resignation ST", "reference_name": doc.name}, ["status", "workflow_state", "modified"]) %}
    <table style="font-size:10pt; color:black; width:100%; border:1px solid black; text-align:center; direction:rtl; margin-top:35px;">
        <thead>
            <tr>
                <td style="border:1px solid black; vertical-align:middle !important; background-color:#d3d3d3; width:30%; font-weight:bold;">ﻣﻦ</td>
                <td style="border:1px solid black; vertical-align:middle !important; background-color:#d3d3d3; width:30%; font-weight:bold;">ﺻﻮﺭﺓ‌‌ ‌‌ﺇﻟﻰ</td>
                <td style="border:1px solid black; vertical-align:middle !important; background-color:#d3d3d3; width:25%; font-weight:bold;">ﻭﻗﺖﺍﻹﺟﺮﺍﺀ</td>
                <td style="border:1px solid black; vertical-align:middle !important; background-color:#d3d3d3; width:15%; font-weight:bold;">ﺍﻹﺟﺮﺍﺀ</td>
            </tr>
        </thead>

        <tbody>
          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">ارسال للاعتماد </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">{{ doc.employee_name }}</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "ارسال للاعتماد" %}
                        {{ frappe.format(d['modified'], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "ارسال للاعتماد" %}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">‌موافقة المدير الإدارة ‌
المعني</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">{{ frappe.db.get_value("Employee", doc.employee_no, "custom_reports_to_employee_name") or ""}}</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة المدير الإدارة المعني"%}
                        {{ frappe.format(d['modified'],"Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة المدير الإدارة المعني"%}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">موافقة المدير العام </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">{{ manager }}</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة المدير العام"%}
                        {{ frappe.format(d["modified"], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة المدير العام"%}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">موافقة مسؤول خدمات الموارد البشرية</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">قاسم اسماعيل بن مصعود طماح 
صالح سعود بن زيد آل مبارك 
</td>
                <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مسؤول خدمات الموارد البشرية"%}
                        {{ frappe.format(d['modified'], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مسؤول خدمات الموارد البشرية"%}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">مراجعة مشرف الرواتب </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">عمر محمد بن سالم الجنوبي </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "مراجعة مشرف الرواتب "%}
                        {{ frappe.format(d['modified'], "Datetime" ) }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "مراجعة مشرف الرواتب "%}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">موافقة مدير عمليات الموارد البشرية</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">وليد سليمان بن زيد الزنيدي </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مدير عمليات الموارد البشرية" %}
                        {{ frappe.format(d['modified'], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مدير عمليات الموارد البشرية" %}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">موافقة مدير عام الموارد البشرية</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">عمر مرزوق بن راضي الهجله 
</td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مدير عام الموارد البشرية" %}
                        {{ frappe.format(d['modified'], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                  {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة مدير عام الموارد البشرية" %}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>

          <tr>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                موافقة نائب الرئيس للعمليات
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">محمد عبدالله عبد العزيز الرشيد </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                   {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة نائب الرئيس للعمليات" %}
                        {{ frappe.format(d["modified"], "Datetime") }}
                    {% endif %}
                  {% endfor %}
              </td>
              <td style="border:1px solid black; vertical-align:middle !important; text-align:center;">
                   {% for d in workflow_data %}
                    {% if d['workflow_state'] == "موافقة نائب الرئيس للعمليات" %}
                        {% if d['status'] == "Completed" %}
                            تمت الموافقة 
                        {% else %}
                            قيد الاعتماد
                        {% endif %}
                    {% endif %}
                  {% endfor %}
              </td>
          </tr>
        </tbody>
    </table>

</div>

{% if print_settings.repeat_header_footer %}
<div id="footer-html">
    {% if footer %}
    <div class="letter-head-footer"> {{ footer }} </div>
    {% endif %}
    <p class="text-center small page-number visible-pdf">
        {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }} </p>
</div>
{% endif %}
