css:
.main-section {
    font-size:13pt;
    color:black !important;
    font-family: 'Arial-MT', Arial, sans-serif;
}

.main-heading {
    font-size:14pt;
    font-weight:bolder;
    text-align:center;
    margin-bottom: 15px !important;
}

.total-row {
    font-weight:bold;
}

.print-format td {
    padding:3px !important;
}

.table {
    width:100%;
    border:1px solid black !important;
    color:black !important;
    text-align:center;
    font-size:12px;
}

