/*    text-align:center;*/
/*}*/

.table tr {
    border: 1px solid black !important;
    color:black !important;
}

.table td {
    border: 1px solid black !important;
    color:black !important;
}

.table-bordered {
    border-width: 1px;
    border-style: solid;
    border-color: black !important;
    color:black !important;
}


.table-bordered tr {
    border: 1px solid black !important;
    color:black !important;
}

.table-bordered td {
    border: 1px solid black !important;
    color:black !important;
}


.header {
    font-weight:bold;
}

.print-format {
    padding-left:0.4in !important;
    padding-right:0.4in !important;
}

@media print{
    .print-format {
        padding:0 !important;
        margin:0 !important;
    }
}
