css:
.header {
      display: flex;
      justify-content: space-between;
    }
.footer {
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
    }
.signature, .stamp {
      text-align: center;
      max-width: 45%;
    }
.signature img, .stamp img {
      max-width: 100%;
      height: auto;
    }
.stamp-title {
      margin-bottom: 10px;
      font-weight: bold;
    }
.arabic, .english{
    padding-left: 0px !important; 
    padding-right: 0px !important;
}
