css:
.ec-container {
    font-size:12pt;
    color:black;
}

.english {
    direction: ltr;
    text-align: left;
}

.arabic {
    direction: rtl;
    text-align: right;
}

table {
    width:100%;
    font-size:12pt;
    color:black;
}


tr { 
    page-break-inside: avoid;
    page-break-after: auto;
}

@media print {
  .page-break {
    page-break-before: always;
  }
}

 li {
    padding-left: 8px;
}
