css:
.main-container {
    color:black;
    font-size:13px;
    font-family: 'Arial-MT', Arial, sans-serif;
    direction:ltr;
}

.main-heading {
    color:black;
    font-weight:bold;
    text-align:center;
    margin-bottom:15px;
}

.total-row {
    font-weight:bold;
}

.table {
    font-size:12px;
    color:black;
    border:1px solid #000 !important;
    width:100%;
    text-align:center;
}

.table-header {
    font-weight:bold;
}

.table tr {
    border:1px solid #000 !important;
}

.table td {
    border:1px solid #000 !important;
}

.print-format {
    padding-right: 0.4in !important;
    padding-left: 0.4in !important;
}

.print-format td {
    padding: 6px !important;
}
